import React, { Suspense, useEffect, useState } from 'react';
import './assets/css/font.css';
import './assets/css/Reset.css';
import './App.css';
import { Header } from './Components/Header/Header';
import { Main } from './Components/Page/MainBlock/Main';
import { Contact } from './Components/Page/Contact/Contact';
import { Why } from './Components/Page/Why/Why';
import { Feature } from './Components/Page/Feature/Feature';
// import { Gallery } from './Components/Page/Gallery/Gallery';
import { Footer } from './Components/Footer/Footer';
import { Indicator } from './Components/Indicator/Indicator';

import { GlobalStyles } from "./Components/globalStyles";
import { lightTheme, darkTheme } from "./Components/Themes"
import { ThemeProvider } from 'styled-components'
const Gallery = React.lazy(() => import('./Components/Page/Gallery/Gallery'));



function App() {

  const [theme, setTheme] = useState('light');
  const [burgerActive, setBurgerActive] = useState(false)

  const themeToggler = () => theme === 'light' ? setTheme('dark') : setTheme('light')







	useEffect(() => {
		const onScroll = e => {
			if (window.pageYOffset > window.innerHeight / 0.25) {
        document.querySelector('.gallery').classList.add("fixed");
      } else {
        document.querySelector('.gallery').classList.remove("fixed");
      }
		};
		window.addEventListener("scroll", onScroll);
		return () => window.removeEventListener("scroll", onScroll);
	}, [])



  return (
    <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
      <>
        <GlobalStyles />
        <div className={"wrapper"}>          
          <Indicator />
          <Header themeToggler={themeToggler} burgerActive={burgerActive} setBurgerActive={setBurgerActive}/>
          <div className="page">
            <Main />
            <Why />
            <Feature />
            <Suspense fallback={<div>Loading...</div>}>
              <Gallery />
            </Suspense>
            <Contact />
          </div>
          <Footer />
        </div>
      </>

    </ThemeProvider >
  );
}

export default App;
