import React, { useRef, useState } from 'react';
import header from './header.module.css'
import {Link} from "react-router-dom";
import AnchorLink from 'react-anchor-link-smooth-scroll'

export const Header = () => {

	// document.querySelector('.header__burger').addEventListener('click', (e) => {
	// 	document.querySelector('.header__menu').classList.toggle('active');
	// 	document.querySelector('.header__burger').classList.toggle('active');
	// 	document.querySelector('body').classList.toggle('lock');
	// })
	
	// let scrollPrev = 0;
	// window.addEventListener('scroll', (e) => {
	// 	let scrolled = window.pageYOffset;
	
	// 	if (scrolled > 100 && scrolled > scrollPrev) {
	// 		document.querySelector('.header').classList.add('out');
	// 	} else {
	// 		document.querySelector('.header').classList.remove('out');
	// 	}
	// 	scrollPrev = scrolled;
	// })	
	const menu = useRef(null)
	const burger = useRef(null)
	const [classBurger, setClassBurger] = useState(header.header__burger)
	const [statusMenu, setStatusBurger] = useState(header.header__burger)
	// const toggleActive = () =>  {
	// 	const css = (menu.current.className === statusBurger) ? statusBurger + ' active' : statusBurger;
	// 	setStatusBurger(css);
	// }

	const handleBurger = () => {
		menu.current.className === header.header__menu ? setStatusBurger((prev)=>prev + ' ' + header.active) : setStatusBurger(header.header__menu);
		burger.current.className === header.header__burger ? setStatusBurger((prev)=>prev + ' ' + header.active) : setStatusBurger(header.header__burger);

		
		
	}

	return (
		<header className={header.header}>
			<div className={header.header__container}>
				<div className={header.header__body}>
					<div className={header.header__column + ' ' + header.header__column_1}>
						<Link to="/" className={header.header__logo}>Medical </Link>
						<div className={header.header__burger} onClick={handleBurger} ref={burger}>
							<span></span>
						</div>
						<nav className={statusBurger} ref={menu}>
							<ul className={header.header__list}>
								<li><Link to="" className={header.header__link}>Home</Link></li>
								<li><Link to="" className={header.header__link}>About us</Link></li>
								<li><Link to="" className={header.header__link}>Services</Link></li>
								<li><Link to="" className={header.header__link}>Portfolio</Link></li>
								<li><Link to="" className={header.header__link}>Blog</Link></li>
								<li><Link to="" className={header.header__link}>Contact</Link></li>
							</ul>
						</nav>
					</div>
					<div className={header.header__column}>
						<AnchorLink href="#form" className={header.header__btn+ " btn"}>Get started</AnchorLink>						
					</div>
				</div>
			</div>
		</header>
	);
};
